using System;

namespace aitg.lib.feature.ba
{
    /// <summary>
    /// Possible weapon subtypes.
    /// </summary>
    public enum WeaponKind
    {
        Dagger,
        Sword,
        Spear,
        Axe,
        Hammer,
        Trophy,
        Stick,
        Gun,
        Bow,
        Claw,
        Crossbow,
        PolearmStab,
        Polearm,
        Wand,
        Slingshot,
        Cannon,
        Staff,
        /// <summary>
        /// Pickaxes.
        /// </summary>
        MiningTool,
        /// <summary>
        /// Any weapon that can be equipped along with the primary:
        /// cards, spells and so on.
        /// </summary>
        SecondaryWeapon
    }

    /// <summary>
    /// What kind of recipe is the item under. Determines recipe level, if
    /// applicable, and the third currency needed to craft.
    /// </summary>
    public enum RecipeType
    {
        Newbie = 1,
        Basic = 3,
        Lrn2Craft = 4,
        Secret = 5,
        Career = 6,
        Kommander = 7,
        Arena = 8,
        Norris = 10,
        Alien = 12,
        EliteAlien = 14,
        Chaotic = 15,
        Unob = 16,
        Enhancer = 17,
        Bacon = 18,
        Reinc = 19,
        Tukk = 21
    }

    /// <summary>
    /// Green bonus.
    /// </summary>
    public enum Green
    {
        Mastery,
        EXP,
        Coin,
        Reward,
        DropRate,
        SpawnRate,
        Pixel,
        EpicMonsterRate,
        WaterElement,
        FireElement,
        IceElement,
        WindElement,
        EarthElement,
        ThunderElement,
        DarkElement,
        LightElement,
        Random,
    }

    /// <summary>
    /// Orange bonus.
    /// </summary>
    public enum Orange
    {
        Attack,
        Autosteal,
        Accuracy,
        BossDamage,
        BlindChance,
        CriticalDamage,
        DoubleHitChance,
        Drain,
        HP,
        InstantKill,
        Mastery,
        MP,
        MPRegen,
        ManaEater,
        PoisonDamage,
        PoisonChance,
        PotionEfficiency,
        SilenceChance,
        StunChance,
        WeakenChance,
        Random,
    }

    /// <summary>
    /// Purple bonus.
    /// </summary>
    public enum Purple
    {
        AttackPower,
        Accuracy,
        DefensePower,
        EquipmentAttack,
        Random,
    }

    /// <summary>
    /// A weapon in BA.
    /// This class is also used for sections
    /// </summary>
    public class Weapon : Item
    {
        public WeaponKind subtype;

        /// <summary>
        /// Minimum rank to use.
        /// </summary>
        public double reqRank;

        /// <summary>
        /// Is the weapon ranged?
        /// </summary>
        public bool range;

        public double attack;
        public double speed;
        public double defense;
        public double crit;
        public double dexterity;
        public double health;

        /// <summary>
        /// Exp required to level up the weapon.
        /// </summary>
        public double expTNL;

        /// <summary>
        /// Green bonus.
        /// </summary>
        public Nullable<Green> bonus;

        /// <summary>
        /// Orange bonus.
        /// </summary>
        public Nullable<Orange> ability;

        /// <summary>
        /// Orange bonus.
        /// </summary>
        public Nullable<Purple> moreBonus;


        /// <summary>
        /// Cannot apply Bonus Rocks?
        /// </summary>
        public bool noBonus;

        /// <summary>
        /// Has an expiration date?
        /// </summary>
        public bool noLife;

        /// <summary>
        /// Is not fusable?
        /// </summary>
        public bool noFuse;

        /// <summary>
        /// Is not uniqueable?
        /// </summary>
        public bool noUnique;

        /// <summary>
        /// Is spirited?
        /// </summary>
        public bool spirit;

        /// <summary>
        /// Bonus tiers.
        /// </summary>
        public double unob;
    }

    public class Item
    {
        public RecipeType recipeType;

        /// <summary>
        /// Recipe level requirement to unlock the item.
        /// Level 1 is always unlocked.
        /// </summary>
        public double recipeReq = 1;

        public double recipeLimit = 1;

        public string name;

        public double frame;

        /// <summary>
        /// Enhancement count.
        /// </summary>
        public double enhance;

        public double bonusPow;

        /// <summary>
        /// Sell value.
        /// </summary>
        public double sell;

        /// <summary>
        /// Expiry time. Infinity if the item never expires.
        /// </summary>
        public double expiry = float.PositiveInfinity;

        public double costPixel;

        /// <summary>
        /// Cost in crafting material.
        /// </summary>
        public double costCraft;

        /// <summary>
        /// Cost in the third currency. Depends on the RecipeType.
        /// </summary>
        public double costSpec;

        public double careerExp;

        /// <summary>
        /// Item description
        /// </summary>
        public string desc;
    }

    public enum SectionLevel
    {
        Title,
        Subtitle,
    }


    /// <summary>
    /// A section that separates items in the shop into different categories.
    ///
    /// For compat reasons, this is kept in the same array as normal items,
    /// and inherits from Item.
    /// </summary>
    public class Section : Item
    {
        public SectionLevel level;

        public string title;
    }

